function add(num1, num2) {
  //TODO 参数校验
  let arr1 = num1.split('').reverse()
  let arr2 = num2.split('').reverse()
  let i = 0;
  let result = [];
  let flag = 0;
  while (arr1[i] || arr2[i]) {
    let a = Number(arr1[i]) || 0;
    let b = Number(arr2[i]) || 0;
    let sum = flag + a + b;
    result[i] = sum % 10;
    flag = ~~(sum / 10);
    i++
  }
  if (flag) {
    result.push(1);
  }
  return result.reverse().join('');
}
